const add = function(a, b) {
	return parseFloat(a) + parseFloat(b);
};

const subtract = function(a, b) {
	return parseFloat(a) - parseFloat(b);
};

const sum = function(numbersArr) {
	return numbersArr.reduce((total, currentNumber) => total + currentNumber, 0);
};

